custom_css:
@import url('https://fonts.googleapis.com/css2?family=Inter:ital,opsz,wght@0,14..32,100..900;1,14..32,100..900&family=JetBrains+Mono:ital,wght@0,100..800;1,100..800&family=Literata:ital,opsz,wght@0,7..72,200..900;1,7..72,200..900&display=swap');

* {
    border-radius: 0 !important; /* Fuck border-radius */
}

/* Variables */
:root {
	/* Fonts */
    --mainFontFamily: "Inter", sans-serif;
    --serifFontFamily: "Literata", serif;
    --monoFontFamily: "JetBrains Mono", monospace;

	/* Colors */
	--quoteThemeColor: #151515;
	--linkThemeColor: #2161a0;
    --black30a: rgba(0, 0, 0, 0.05);
    --golden: #DC143C;
}

/* Extension Manager Variables */
.stem--tabUi {
  --primaryBgColRgb: var(--primaryBgCol);
  --bgColor: var(--SmartThemeBlurTintColor);
  --accentColor: var(--SmartThemeQuoteColor);
}

/* Fonts */
body {
    font-family: var(--mainFontFamily), sans-serif;
}

code {
    font-family: var(--monoFontFamily), monospaced;
    font-size: 0.9rem;
}

/* General UI */
.red_button {
    color: var(--SmartThemeBlurTintColor);
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
	background: none;
	border: none;
}

.drawer-content {
	padding: 1em;
	border: none;
}

.fillLeft, .fillRight {
	max-height: 100%;
}

.chatMessage {
	font-family: var(--serifFontFamily), serif;
    font-size: 1rem;
}

.mes_button {
    filter: none;
}

.mes_text {
	font-family: var(--serifFontFamily), serif;
    font-size: 1rem;
    line-height: 1.4rem;
}

.mes_text blockquote{
	padding: 1em;
	margin: 1em 0;
}

.mes_text a {
	color: var(--linkThemeColor);
	text-decoration: underline;
}

.mes_text a:hover {
	color: var(--linkThemeColor);
	text-decoration: none;
}

.mes_text hr {
	background-image: none;
	border: 1px solid var(--SmartThemeBodyColor);
	margin: 1em 2em;;
	opacity: 0.7;
}

.mes_reasoning_header {
    background: var(--SmartThemeChatTintColor);
    border: 1px solid var(--SmartThemeBorderColor);;
}

.mes_reasoning {
    font-family: var(--serifFontFamily);
    font-size: 1rem;
}

.mes_edit_cancel.menu_button {
    color: var(--SmartThemeBlurTintColor);
}

.mes_edit_done.menu_button {
    color: var(--SmartThemeBlurTintColor);
}

.timestamp {
    font-size: 0.8rem;
}

.edit_textarea {
    background-color: var(--black30a);
}

#send_form {
    border: none;
}

#completion_prompt_manager .completion_prompt_manager_header_advanced span {
    filter: none;
}

#completion_prompt_manager .completion_prompt_manager_header {
    color: var(--SmartThemeBodyColor);
}

#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name,
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action {
    color: var(--black50a);
}

#completion_prompt_manager #completion_prompt_manager_list li.completion_prompt_manager_prompt span span span {
    filter: none;
}

#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt .completion_prompt_manager_prompt_name .fa-solid {
    color: var(--black50a);
}

.popup {
    box-shadow: none;
}

.menu_button.popup-button-ok {
    color: var(--SmartThemeChatTintColor);
}

.menu_button.popup-button-ok:hover {
    color: var(--SmartThemeBodyColor);
}

/* Character Drawer */
#bulkTagsList,
#tagList .tag,
#groupTagList .tag {
    opacity: 1;
    border: none;
}

/* Background Image */
#bg1 {
    filter: blur(var(--SmartThemeBlurStrength));
    opacity: 0.5;
}

/* Extension Manager */
.stem--tabUi .stem--tabRow .stem--tab.stem--isSelected {
	border: 1px solid var(--SmartThemeBodyColor);
}

.stem--tabUi .stem--tabRow .stem--tab.stem--isSelected {
	border-bottom-color: var(--bgColor);
}

.stem--tabUi .stem--tabRow .stem--tab.stem--isSelected::after {
	background: none;
}

.stem--tabUi .stem--tabRow .stem--tab.stem--isSelected::before{
	background: none;
}

/* Top Bar */
#extensionTopBar {
	border: none !important;
	font-size: 1rem;
}

#top-bar {
    box-shadow: none;
}

/* World Info Info */
.stwii--trigger {
	z-index: 0 !important;
}
