custom_css:
@import url('https://fonts.googleapis.com/css2?family=Inter:ital,opsz,wght@0,14..32,100..900;1,14..32,100..900&family=JetBrains+Mono:ital,wght@0,100..800;1,100..800&family=Source+Serif+4:ital,opsz,wght@0,8..60,200..900;1,8..60,200..900&display=swap');

* {
    border-radius: 0 !important; /* Fuck border-radius */
}

/* Variables */
:root {
	/* Fonts */
    --mainFontFamily: "Inter", sans-serif;
    --serifFontFamily: "Source Serif 4", serif;
    --monoFontFamily: "JetBrains Mono", monospace;

	/* Colors */
	--quoteThemeColor: #151515;
	--linkThemeColor: #318CE7;
}

/* Extension Manager Variables */
.stem--tabUi {
  --primaryBgColRgb: var(--primaryBgCol);
  --bgColor: var(--SmartThemeBlurTintColor);
  --accentColor: var(--SmartThemeQuoteColor);
}

/* Fonts */
body {
    font-family: var(--mainFontFamily), sans-serif;
}

code {
    font-family: var(--monoFontFamily), monospaced;
    font-size: 0.9rem;
}

/* General UI */
#extensions_settings .inline-drawer-toggle.inline-drawer-header,
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header,
#user-settings-block h4,
.standoutHeader {
	background: none;
	border: none;
}

.drawer-content {
	padding: 1em;
	border: none;
}

.fillLeft, .fillRight {
	max-height: 100%;
}

.chatMessage {
	font-family: var(--serifFontFamily), serif;
    font-size: 1rem;
}

.mes_text {
	font-family: var(--serifFontFamily), serif;
    font-size: 1rem;
    line-height: 1.4rem;
}

.mes_text blockquote{
	background-color: var(--quoteThemeColor);
	padding: 1em;
	margin: 1em 0;
}

.mes_text a {
	color: var(--linkThemeColor);
	text-decoration: underline;
}

.mes_text a:hover {
	color: var(--linkThemeColor);
	text-decoration: none;
}

.mes_text hr {
	background-image: none;
	border: 1px solid var(--SmartThemeBodyColor);
	margin: 1em 2em;;
	opacity: 0.7;
}

.mes_reasoning_header {
    background: var(--SmartThemeChatTintColor);
    border: 1px solid var(--SmartThemeBorderColor);;
}

.mes_reasoning {
    font-family: var(--serifFontFamily);
    font-size: 1rem;
}

.timestamp {
    font-size: 0.8rem;
}

#send_form {
    border: none;
}

/* Character Tags */
#bulkTagsList,
#tagList .tag,
#groupTagList .tag {
    opacity: 1;
    border: none;
}

/* Background Image */
#bg1 {
    filter: blur(var(--SmartThemeBlurStrength));
    opacity: 0.5;
}

/* Extension Manager */
.stem--tabUi .stem--tabRow .stem--tab.stem--isSelected {
	border: 1px solid var(--SmartThemeBodyColor);
}

.stem--tabUi .stem--tabRow .stem--tab.stem--isSelected {
	border-bottom-color: var(--bgColor);
}

.stem--tabUi .stem--tabRow .stem--tab.stem--isSelected::after {
	background: none;
}

.stem--tabUi .stem--tabRow .stem--tab.stem--isSelected::before{
	background: none;
}

/* Top Bar */
#extensionTopBar {
	border: none;
	font-size: 1rem;
}

/* RPG Companion */
.rpg-panel {
	font-family: var(--mainFontFamily), sans-serif !important;
}

.rpg-stat-label, .rpg-stat-value {
	color: white !important;
	mix-blend-mode: difference;
}

.rpg-stat-bar {
	box-shadow: none !important;
	border: none !important;
}

.rpg-events-widget {
	background: none !important;
}

.rpg-map-bg {
	background: none !important;
}

/* World Info Info */
.stwii--trigger {
	z-index: 0 !important;
}
